import React, { useState } from 'react';
import './App.css';

const RSAEncryption: React.FC = () => {
    const [p, setP] = useState<number>(0);
    const [q, setQ] = useState<number>(0);
    const [e, setE] = useState<number>(0);
    const [m, setM] = useState<number>(0);
    const [cipherText, setCipherText] = useState<string | null>(null);
    const [originalMessage, setOriginalMessage] = useState<number | null>(null);
    const [result, setResult] = useState<string | null>(null);
    const [error, setError] = useState<string | null>(null);

    const handleEncrypt = async () => {
        setError(null); // Reset error trước mỗi lần gọi API
        setResult(null); // Reset result trước mỗi lần gọi API
        try {
            const response = await fetch('http://localhost:5000/encrypt', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ p, q, e, m }),
            });

            const data = await response.json();

            // Xử lý phản hồi từ API
            if (data.result !== "Successful") {
                setError(data.result); // Hiển thị lỗi nếu result không phải "Successful"
                setCipherText(null);
                setOriginalMessage(null);
            } else {
                setCipherText(data.cipherText);
                setOriginalMessage(data.originalMessage);
                setResult(data.result); // Hiển thị kết quả thành công
            }
        } catch (err) {
            console.error('Error:', err);
            setError('Failed to connect to server.');
        }
    };

    return (
        <div className="container">
            <h1>RSA Encryption</h1>
            <h2>Step 1: Set prime numbers</h2>
            <div className="input-group">
                <label>p:</label>
                <input type="number" value={p} onChange={(e) => setP(Number(e.target.value))}/>
            </div>
            <div className="input-group">
                <label>q:</label>
                <input type="number" value={q} onChange={(e) => setQ(Number(e.target.value))}/>
            </div>
            <h2>Step 2: The number e can now be chosen freely. However, it must be coprime to ϕ(n) = (p-1)(q-1)</h2>
            <div className="input-group">
                <label>e:</label>
                <input type="number" value={e} onChange={(e) => setE(Number(e.target.value))}/>
            </div>
            <h2>Step 3: Enter the message</h2>
            <div className="input-group">
                <label>m:</label>
                <input type="number" value={m} onChange={(e) => setM(Number(e.target.value))}/>
            </div>
            <button onClick={handleEncrypt}>Encrypt</button>

            {/*/!* Hiển thị lỗi nếu có *!/*/}
            {/*{error && <p style={{color: 'red'}}>Error: {error}</p>}*/}

            {/*/!* Hiển thị kết quả nếu thành công *!/*/}
            {result === "Successful" && (
                <div>
                    <h2>Results:</h2>
                    <p>
                        <strong>Encrypted Message:</strong> {cipherText}
                    </p>
                    <p>
                        <strong>Original Message:</strong> {originalMessage}
                    </p>
                    <p style={{color: 'green'}}>
                        <strong>Result:</strong> {result}
                    </p>
                </div>
            )}
        </div>
    );
};

export default RSAEncryption;
