import React, { useState } from 'react';
import axios from 'axios';

interface ResponseData {
  encrypted_message: number;
  error?: string;
}

const App: React.FC = () => {
  const [p, setP] = useState<number | string>('');
  const [q, setQ] = useState<number | string>('');
  const [e, setE] = useState<number | string>('');
  const [m, setM] = useState<number | string>('');
  const [result, setResult] = useState<number | null>(null);
  const [error, setError] = useState<string | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (typeof p !== 'number' || typeof q !== 'number' || typeof e !== 'number' || typeof m !== 'number') {
      setError('Please enter valid numbers');
      return;
    }

    try {
      const response = await axios.post<ResponseData>('http://127.0.0.1:5000/api/rsa', {
        p: Number(p),
        q: Number(q),
        e: Number(e),
        m: Number(m),
      });

      if (response.data.error) {
        setError(response.data.error);
      } else {
        setResult(response.data.encrypted_message);
        setError(null);
      }
    } catch (error) {
      console.error(error);
      setError('Error calculating!');
    }
  };

  return (
      <div>
        <h1>RSA Encryption</h1>
        <form onSubmit={handleSubmit}>
          <input
              type="number"
              value={p}
              onChange={(e) => setP(Number(e.target.value))}
              placeholder="Enter p (prime)"
          />
          <input
              type="number"
              value={q}
              onChange={(e) => setQ(Number(e.target.value))}
              placeholder="Enter q (prime)"
          />
          <input
              type="number"
              value={e}
              onChange={(e) => setE(Number(e.target.value))}
              placeholder="Enter e"
          />
          <input
              type="number"
              value={m}
              onChange={(e) => setM(Number(e.target.value))}
              placeholder="Enter m (message)"
          />
          <button type="submit">Encrypt</button>
        </form>

        {error && <p style={{ color: 'red' }}>{error}</p>}
        {result !== null && <h2>Encrypted Message: {result}</h2>}
      </div>
  );
};

export default App;
