import React, { useState } from 'react';
import axios from 'axios';

interface ResponseData {
  result: number;
}

const App: React.FC = () => {
  const [a, setA] = useState<number | string>('');
  const [b, setB] = useState<number | string>('');
  const [result, setResult] = useState<number | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (typeof a !== 'number' || typeof b !== 'number') {
      alert('Please enter valid numbers');
      return;
    }

    try {
      const response = await axios.post<ResponseData>('http://127.0.0.1:5000/api/calculate', {
        a: a,
        b: b,
      });
      setResult(response.data.result); // Lưu kết quả vào state
    } catch (error) {
      console.error(error);
      alert('Error calculating!');
    }
  };

  return (
      <div>
        <h1>Calculate Sum</h1>
        <form onSubmit={handleSubmit}>
          <input
              type="number"
              value={a}
              onChange={(e) => setA(Number(e.target.value))}
              placeholder="Enter A"
          />
          <input
              type="number"
              value={b}
              onChange={(e) => setB(Number(e.target.value))}
              placeholder="Enter B"
          />
          <button type="submit">Calculate</button>
        </form>
        {result !== null && <h2>Result: {result}</h2>}
      </div>
  );
};

export default App;
