import React, { useState } from 'react';
import axios from 'axios';

interface ResponseData {
  encrypted_message: number;
  error?: string;
}

const App: React.FC = () => {
  const [p, setP] = useState<string>('');
  const [q, setQ] = useState<string>('');
  const [e, setE] = useState<string>('');
  const [m, setM] = useState<string>('');
  const [result, setResult] = useState<number | null>(null);
  const [error, setError] = useState<string | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Chuyển giá trị nhập vào thành số
    const pNum = Number(p);
    const qNum = Number(q);
    const eNum = Number(e);
    const mNum = Number(m);

    // Kiểm tra nếu các giá trị không hợp lệ
    // if (isNaN(pNum) || isNaN(qNum) || isNaN(eNum) || isNaN(mNum)) {
    //   setError('Please enter valid numbers');
    //   return;
    // }

    try {
      const response = await axios.post<ResponseData>('http://127.0.0.1:5000/api/rsa', {
        p: pNum,
        q: qNum,
        e: eNum,
        m: mNum,
      });

      if (response.data.error) {
        setError(response.data.error);
      } else {
        setResult(response.data.encrypted_message);
        setError(null);
      }
    } catch (error) {
      console.error(error);
      setError('Error calculating!');
    }
  };

  return (
      <div>
        <h1>RSA Encryption</h1>
        <form onSubmit={handleSubmit}>
          <input
              type="number"
              value={p}
              onChange={(e) => setP(e.target.value)}
              placeholder="Enter p (prime)"
          />
          <input
              type="number"
              value={q}
              onChange={(e) => setQ(e.target.value)}
              placeholder="Enter q (prime)"
          />
          <input
              type="number"
              value={e}
              onChange={(e) => setE(e.target.value)}
              placeholder="Enter e"
          />
          <input
              type="number"
              value={m}
              onChange={(e) => setM(e.target.value)}
              placeholder="Enter m (message)"
          />
          <button type="submit">Encrypt</button>
        </form>

        {error && <p style={{ color: 'red' }}>{error}</p>}
        {result !== null && <h2>Encrypted Message: {result}</h2>}
      </div>
  );
};

export default App;
